export const AddressWidget = forwardRef((props, ref) => {
  const [showPopup, setPopup] = useState(false);
  const [visibleTreeData, setVisibleTreeData] = useState([]);
  const [pathNames, setPathNames] = useState([]);
  const [searchValue, setSearchValue] = useState('');
  const [textToShow, setTextToShow] = useState('');
  const [showSearchedData, setShowSearchedData] = useState(false);
  const [searchedData, setSearchedData] = useState([]);
  const wrapperRef = useRef(null);
  const {
    addressTree = [],
    placeholder,
    name,
    error,
    helperText,
    showRequiredIcon,
    label = undefined,
    onChange,
    hideErrorText = false,
    extraStyle = {},
    fakeInputStyle = {},
    disabled,
  } = props;
  const classes = cx({
    required: showRequiredIcon,
    'has-errors': error,
  });

  const fakeInputClasses = cx({
    'fake-input': true,
    'has-errors': error,
  });

  const handleSearch = async (e) => {
    const { value } = e.target;
    setSearchValue(value);
    if (value.length < 3) {
      return;
    }

    if (value) {
      const searchedAreas = await areaAPI.searchAreasV2(value);
      setSearchedData(searchedAreas);
    }
  };

  const calculateLeaf = (items = []) => {
    return items.length;
  };

  const closePopup = useCallback(() => {
    if (addressTree) {
      setPopup(false);
      setVisibleTreeData(addressTree.Divisions);
      setPathNames([]);
      setSearchValue('');
    }
  }, [addressTree]);

  const findArea = function (collection, idToFind, nodeToFind) {
    if (collection !== undefined) {
      collection.map((item) => {
        if (item.ID === idToFind && item.nodeType === nodeToFind) {
          setVisibleTreeData(item.Districts || item.Areas);
        } else {
          findArea(item.Districts || item.Areas, idToFind, nodeToFind);
        }
      });
    }
  };

  const onTreeDataClick = (data) => {
    console.log(data);
    if (data.Districts || data.Areas) {
      setSearchValue('');
      setVisibleTreeData(data.Districts || data.Areas);
      setPathNames([
        ...pathNames,
        { NAME: data.NAME, ID: data.ID, nodeType: data.nodeType },
      ]);

function AgentStatus({ isActive, agentId, type }) {
  const [isSubmitting, setIsSubmitting] = useState(false);

  const dispatch = useDispatch();
  const handleChange = useCallback(async () => {
    try {
      setIsSubmitting(true);
      if (type === 'hyperlocal') {
        await dispatch(updateHyperlocalAgentStatus(agentId, { isActive: !isActive }));
      } else {
        await dispatch(updateAgentStatus(agentId, { isActive: !isActive }));
      }
    } catch (error) {
      message.error(
        error?.response?.data?.message ||
          error?.response?.message ||
          'An error occurred.',
      );
    }
    setIsSubmitting(false);
  }, [agentId, isActive, type, dispatch]);

  return <Switch loading={isSubmitting} checked={isActive} onChange={handleChange} />;
}

export function FileUpload(props: FileUploadProps) {
