const AntInput = ({
  name,
  control,
  defaultValue,
  rules,
  error,
  className,
  style = {},
  placeholder,
  validateStatus,
  onKeyDown,
  help,
  autoFocus,
}) => {
  return (
    <Form.Item
      validateStatus={error ? 'error' : validateStatus}
      hasFeedback={!!error || !!help || !!validateStatus}
      style={{ ...style, margin: 0 }}
      help={error?.message || help}
      onKeyDown={onKeyDown}>
      <Controller
        placeholder={placeholder}
        className={className}
        as={<Input />}
        name={name}
        control={control}
        defaultValue={defaultValue}
        rules={rules}
        disabled={validateStatus === 'validating'}
        autoFocus={autoFocus}
      />
    </Form.Item>
  );
};

const AreaSearch = (props) => {
  const [isLoading, setIsLoading] = useState(false);
  const [keyword, setKeyword] = useState('');
  const [areas, setAreas] = useState([]);
  const handleSearch = async (keyword) => {
    setIsLoading(true);
    try {
      let matchedAreas = await areaAPI.searchAreasV2(keyword);
      let _areas = matchedAreas.map((mArea) => mArea.AREAS);
      setAreas(flatten(_areas));
    } catch (error) {
      console.error('> error > AreaSearch > handleSearch', error);
    }
    setIsLoading(false);
  };

  const debouncedSearch = debounce(handleSearch, 500);

  useEffect(() => {
    if (!keyword) return;
    debouncedSearch(keyword);
    return debouncedSearch.cancel;
  }, [keyword]);

  return (
    <Select
      placeholder="Area"
      showSearch
      notFoundContent={isLoading ? <Spin size="small" /> : null}
      filterOption={false}
      onSearch={setKeyword}
      loading={isLoading}
      allowClear
      autoComplete="off"
      {...props}>
      {areas.map((sp) => (
        <Option key={sp.ID} value={sp.ID}>
          {sp.NAME}
        </Option>
      ))}
    </Select>
  );
};

const BranchOfBankSearch = ({
  bankId,
  onSelect,
  onChange,
  initialBranches,
  disabledSearch,
  ...props
}) => {
  const [isLoading, setIsLoading] = useState(false);
  const [keyword, setKeyword] = useState('');
  const [branches, setBranches] = useState(initialBranches || []);
  const initialBankId = useRef(bankId);

  useEffect(() => {
    if (initialBankId.current && initialBankId.current !== bankId) {
      setBranches([]);
    }
    initialBankId.current = bankId;
  }, [bankId]);

  const handleSearch = async (keyword) => {
    setIsLoading(true);
    try {
      let matchedBranches = await bankAPI.searchBranchesByBankId(bankId, keyword);
      setBranches(matchedBranches);
    } catch (error) {
      console.error('> error > BranchOfBankSearch > handleSearch', error);
    }
    setIsLoading(false);
  };

  const debouncedSearch = debounce(handleSearch, 500);

  useEffect(() => {
    if (!keyword) return;
    debouncedSearch(keyword);
    return debouncedSearch.cancel;
  }, [keyword]);

  return (
    <Select
      showSearch
      placeholder="Search Branch"
      notFoundContent={isLoading ? <Spin size="small" /> : null}
      filterOption={false}
      onSearch={setKeyword}
      loading={isLoading}
      allowClear
      autoComplete="off"
      disabled={!bankId || disabledSearch}
      onChange={(branchId) => {
        onChange(branchId);
        if (branchId) {
          const branch = branches.find((bn) => bn.ID === branchId);
          onSelect(branch);
        }
      }}
      {...props}>
      {branches.map((sp) => (
        <Option key={sp.ID} value={sp.ID}>
          {sp.BRANCH_NAME}
        </Option>
      ))}
    </Select>
  );
};

