const FinanceFileUpload = ({
  title,
  subTitle,
  contentLabel,
  attachmentProofLabel,
  attachmentCsvLabel,
  sampleFileLink,
  csvUploadParam,
  type,
}: FinanceUploadProps) => {
  const [loading, setLoading] = useState(false);
  const [imageUrl, setImageUrl] = useState('');
  const [financeForm] = Form.useForm();

  const getBase64 = (img: RcFile, callback: (url: string) => void) => {
    const reader = new FileReader();
    reader.addEventListener('load', () => callback(reader.result as string));
    reader.readAsDataURL(img);
  };

  const handleImageChange: UploadProps['onChange'] = async (
    info: UploadChangeParam<UploadFile>,
  ) => {
    if (info.file.status === 'uploading') {
      setLoading(true);
      return;
    }

    if (info.file.status === 'done') {
      getBase64(info.file.originFileObj as RcFile, (url) => {
        setImageUrl(url);
      });
      setLoading(false);
      return;
    }

    if (info.file.status === 'error') {
      message.error(`${info.file.name} image upload failed.`);
      setLoading(false);
    }
  };

  const handleFileUpload: UploadProps['onChange'] = async (
    info: UploadChangeParam<UploadFile>,
  ) => {
    const { file } = info;
    if (file.status === 'uploading') {
      setLoading(true);
      return;
    }

    if (file.status === 'done') {
      setLoading(false);
      return;
    }

    if (file.status === 'error') {
      message.error(`${file.name} file upload failed.`);
      setLoading(false);
    }
  };

  const removeUploadedFile = async () => {
    setLoading(true);
    await financeForm.setFieldsValue({
      'csv-file': undefined,
    });
    setLoading(false);
  };

  const onFinish = async (values: any) => {
    setLoading(true);
    try {
      const csvFormData = new FormData();

      if (type === 'jv') {
        if (attachmentProofLabel) {
          const imageFormData = new FormData();
          imageFormData.append('image-file', values['image-file'].file.originFileObj);
          const res: any = await financeAPI.uploadInvoiceImage(imageFormData);

