const NavbarLink = ({ url, title }) => {
  let target = '';
  let href = '';

  if (url.includes('http')) {
    target = '_blank';
    href = url;
    url = '';
  }

  return (
    <Link href={url}>
      <a target={target} href={href}>
        {title}
      </a>
    </Link>
  );
};

