        {modalState.data?.shops?.map((shop: any) => (
          <Checkbox
            key={shop.shopId}
            value={shop.shopId}
            data-testid={`PostPaidSettlementsCreateInvoiceModal_Shop_${shop.shopId}`}>
            {shop.shopName}
          </Checkbox>
        ))}
      </Checkbox.Group>
      <span className="bold-text">
        PLease do not close this window. Background process is running....
      </span>
    </Modal>
  );
};

