export const productReducer = (state, action) => {
  switch (action.type) {
    case 'CLOSE_POPUP':
      return {
        ...state,
        showPopup: false,
        visibleTreeData: action.payload.visibleTreeData,
        pathNames: [],
        searchValue: '',
      };
    case 'OPEN_POPUP':
      return {
        ...state,
        showPopup: true,
      };
    case 'ON_DATA_CLICK':
      return {
        ...state,
        visibleTreeData: action.payload.visibleTreeData,
        pathNames: action.payload.pathNames,
        searchValue: '',
      };
    case 'ON_BACK_CLICK':
      return {
        ...state,
        visibleTreeData: action.payload.visibleTreeData,
        pathNames: [],
        searchValue: '',
        showProductName: true,
        searchedData: [],
        showSearchedData: false,
      };
    case 'INITIAL_TREE':
      return {
        ...state,
        visibleTreeData: action.payload.visibleTreeData,
      };
    case 'ON_SEARCH_DATA_CLICK':
      return {
        ...state,
        showProductName: false,
        pathNames: action.payload.pathNames,
        searchedData: action.payload.searchedData,
        textRefresh: true,
      };
    case 'SHOW_SEARCH':
      return {
        ...state,

        showSearchedData: true,
      };
    case 'NO_SEARCH':
      return {
        ...state,
        showSearchedData: action.payload.showSearchedData,
        requiredData: [],
      };
    case 'SET_SEARCHED_VALUE':
      return {
        ...state,
        searchValue: action.payload.searchValue,
        textRefresh: false,
      };
    case 'FINAL_TEXT':
      return {
        ...state,
        textToShow: action.payload.textToShow,
      };
    case 'FINAL_SEARCH_TEXT':
      return {
        ...state,
        searchedData: [],
        showProductName: true,
        textToShow: action.payload.textToShow,
      };
    case 'SEARCHED_CATEGORY':
      return {
        ...state,
        searchedData: action.payload.searchedData,
      };

export const SearchableSelect = ({
  value,
  onChange,
  placeholder,
  className = '',
  onSearch,
  data = [],
  showRequiredIcon,
  error,
  antStyles = false,
  label = undefined,
  showArrow = false,
  suffixIcon = null,
  ...restProps
}) => {
  const classes = [className];
  showRequiredIcon && classes.push('required');
  error && classes.push('has-errors');
  const isGrouped = data.length > 0 && data[0].groupId;
  return (
    <div className={classes.join(' ').trim()} css={!antStyles && select_css}>
      {label && <p className="label">{label}</p>}
      <AntSelect
        showSearch
        value={value}
        placeholder={placeholder}
        defaultActiveFirstOption
        showArrow={showArrow}
        filterOption={false}
        onSearch={onSearch}
        onChange={onChange}
        notFoundContent={null}
        suffixIcon={<Icon type="search" />}
        {...restProps}>
        {isGrouped
          ? data.map((group) => (
              <OptGroup
                label={<p style={{ fontWeight: 'bold' }}>{group.groupLabel}</p>}
                key={group.groupId}>
                {group.options.map((option) => (
                  <Option key={option.value} title={option.text} value={option.value}>
                    {option.text}
                  </Option>
                ))}
              </OptGroup>
            ))
          : data.map((d) => (
              <Option key={d.value} title={d.text} value={d.value}>
                {d.text}
              </Option>
            ))}
      </AntSelect>
      {error && <div className="fieldError">{error.message}</div>}
    </div>
  );
};

export function SelectMultiSearch(props: SelectMultiSearchProps) {
  const { dataList, idKey, titleKey, searchKeys, opts, value, onChange } = props;
  const [data, setData] = useState(dataList);
  useEffect(() => {
    setData(dataList);
  }, [dataList]);
  const validate = (value: string, keyword: string) => {
    const regex = new RegExp(`^${keyword}`, 'i');
    return regex.test(value);
  };
  const onSearch = (keyword: string) => {
    const newData = dataList.filter((rec) => {
      for (let i = 0; i < searchKeys.length; i++) {
        if (rec[searchKeys[i]] && validate(rec[searchKeys[i]], keyword)) {
          return true;
        }
      }
      return false;
    });
    setData(newData);
  };
  const handleClear = () => {
    setData(dataList);
  };
  return (
    <Select
      value={value}
      onChange={onChange}
      placeholder={opts.placeHolder || 'Type something...'}
      showSearch
      onSearch={onSearch}
      filterOption={false}
      onClear={handleClear}
      allowClear={opts.allowClear}>
      {data &&
        data.map((sp) => (
          <Option key={sp[idKey]} value={sp[idKey]} label={sp[titleKey]}>
            <Tag>{sp[idKey]}</Tag> {sp[titleKey]}
          </Option>
        ))}
    </Select>
  );
}

