const ShopSearch = (props) => {
  const [isLoading, setIsLoading] = useState(false);
  const [keyword, setKeyword] = useState('');
  const [shops, setShops] = useState([]);
  const handleSearch = async (keyword) => {
    setIsLoading(true);
    try {
      if (props.searchOnlyId) {
        if (isNaN(keyword)) {
          throw new Error('You can only search using Shop ID');
        }
      }
      const matchedShops = await shopAPI.searchShop(keyword);
      setShops(matchedShops);
    } catch (error) {
      message.error(error.message);
      console.error('> error > ShopSearch > handleSearch', error);
    }
    setIsLoading(false);
  };

  const debouncedSearch = debounce(handleSearch, 500);

  useEffect(() => {
    if (!keyword) return;
    debouncedSearch(keyword);
    return debouncedSearch.cancel;
  }, [keyword]);
  const validClear = props.onClear ? props.onClear : null;
  return (
    <Select
      placeholder="Merchant"
      showSearch
      notFoundContent={isLoading ? <Spin size="small" /> : null}
      filterOption={false}
      onSearch={setKeyword}
      loading={isLoading}
      allowClear
      onClear={validClear}
      defaultValue={
        props.shopInfoFilter ? (
          <>
            <Tag>{props.shopInfoFilter?.id}</Tag> {props.shopInfoFilter?.label}
          </>
        ) : undefined
      }
      {...props}
      data-testid="ShopSearch">
      {shops?.map((sp) => (
        <Option key={sp.id} value={sp.id} label={sp.name} data-testid="ShopSearch_Option">
          <Tag>{sp.id}</Tag> {sp.name}
        </Option>
      ))}
    </Select>
  );
};

const ShopSearchV2 = (props: ShopSearchV2Props & SelectProps<SelectValue>) => {
  const [isLoading, setIsLoading] = useState(false);
  const [shops, setShops] = useState<ShopResponseType[]>([]);

  const handleSearch = async (keyword: string) => {
    setIsLoading(true);
    try {
      const matchedShops = await shopAPI.searchShop(keyword);
      setShops(matchedShops);
    } catch (error) {
      console.error('> error > ShopSearch > handleSearch', error);
    }
    setIsLoading(false);
  };

  const debouncedSearch = debounce(handleSearch, 500);

  const validClear = props.onClear ? props.onClear : undefined;

  const handleShopSelection: SelectProps<SelectValue>['onChange'] = (value) => {
    if (props.onChange) {
      props.onChange(value);
    }

    if (props.onShopSelect) {
      props.onShopSelect();
    }
  };

  return (
    <Select
      {...props}
      showSearch
      notFoundContent={isLoading ? <Spin size="small" /> : null}
      filterOption={false}
      onChange={handleShopSelection}
      onSearch={(value) => debouncedSearch(value)}
      loading={isLoading}
      allowClear
      onClear={validClear}>
      {shops.map((sp) => (
        <Select.Option key={sp.id} value={sp.id} label={sp.name}>
          <Tag>{sp.id}</Tag> {sp.name}
        </Select.Option>
      ))}
    </Select>
  );
};

