export function useAntNotificationAlert(status, message, reset, style = {}) {
  const dispatch = useDispatch();
  useEffect(() => {
    if (!status || !message) {
      return;
    }
    if (status === 'rejected') {
      notification.error({
        message,
        style,
      });
    } else if (status === 'fulfilled') {
      notification.success({
        message,
        style,
      });
    }
    if (reset) {
      setTimeout(() => {
        dispatch(reset());
      }, 2000);
    }
  }, [status, message, reset, dispatch]);
}

export function useAuthorization({ permissionIds, roles }: UseAuthorizationPropType) {
  const { userPermissions, user } = useSelector((state: any) => state.auth);

  const isAuthorized = useMemo(() => {
    if (!roles && !permissionIds) return true;

    // roles will have precedence
    if (roles) {
      return (user?.Roles || []).find((userRole: UserRoleType) =>
        Array.isArray(roles) ? roles.includes(userRole.name) : roles === userRole.name,
      );
    }

    return (userPermissions || []).find((userPermission: UserPermissionType) =>
      Array.isArray(permissionIds)
        ? permissionIds.includes(userPermission.id)
        : userPermission.id === permissionIds,
    );
  }, [userPermissions, permissionIds, user, roles]);

  return !!isAuthorized;
}

export function useFilterValues({ defaultValues = {} } = {}) {
  const [filterValues, setFilterValues] = useState({});

  // WARNING: pass values always even if its undefined, as merging with states
  const onFilterChange = useCallback((params) => {
    setFilterValues((state) => ({ ...state, ...params }));
  }, []);

  const queryObject = useMemo(() => ({ ...defaultValues, ...filterValues }), [
    filterValues,
    defaultValues,
  ]);

  return { onFilterChange, filterValues, queryObject };
}

