export function useGetAreas() {
  const [areas, setAreas] = useState<AREASTYPE[]>();

  useEffect(() => {
    getAllAreas();
  }, []);

  const getAllAreas = async () => {
    try {
      const res: AxiosResponse<SuccessResponseType> = await areaAPI.getAreas();

      const areas = res.data.body.areas;

      setAreas(areas);
    } catch (error: any) {
      message.error(error.message || 'Something went wrong on the server');
    }
  };

  const fetch = (value: string, callback: CallableFunction) => {
    const searchAreas = async () => {
      if (value.length <= 2) return;

      const matchedAreas:
        | AxiosResponse<SuccessResponseType>
        | undefined = await areaAPI.getAreas('', value);

      callback(matchedAreas?.data.body.areas);
    };

    const searchAreasDebounced = lodash.debounce(searchAreas, 500);

    searchAreasDebounced();
  };

  const handleSearch = async (value: string) => {
    if (value) {
      fetch(value, (data: AREASTYPE[]) => setAreas(data));
    } else {
      getAllAreas();
    }
  };

  return { areas, handleSearch };
}

