export function useModalState(
  initialState = {
    isOpen: false,
    data: {},
  },
) {
  const _intialValuesOfData = { ...initialState.data };

  const OPEN_MODAL = 'OPEN_MODAL';
  const CLOSE_MODAL = 'CLOSE_MODAL';
  const UPDATE_DATA = 'UPDATE_DATA';

  function reducer(state, action) {
    switch (action.type) {
      case OPEN_MODAL:
        return {
          ...state,
          isOpen: true,
          data: action.payload,
        };
      case CLOSE_MODAL:
        return { ...state, isOpen: false, data: _intialValuesOfData };
      case UPDATE_DATA:
        return {
          ...state,
          data: action.payload,
        };
      default:
        throw new Error();
    }
  }

  const [state, dispatch] = useReducer(reducer, initialState);

  const openModal = (payload) => {
    dispatch({
      type: OPEN_MODAL,
      payload,
    });
  };

  const closeModal = () => {
    dispatch({ type: CLOSE_MODAL });
  };

  const updateData = (payload) => {
    dispatch({
      type: UPDATE_DATA,
      payload,
    });
  };

  return {
    state,
    openModal,
    closeModal,
    updateData,
  };
}

