export function useResizeEvent() {
  const [isMobileView, setMobileView] = useState(false);

  useEffect(() => {
    if (window.innerWidth < 992) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
    const handleResize = () => {
      if (window.innerWidth < 992) {
        setMobileView(true);
      } else {
        setMobileView(false);
      }
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return isMobileView;
}

