export function useTable<
  ParametersType,
  SuccessResponseType,
  ErrorResponseType,
  DataType
>(props: Props<ParametersType, SuccessResponseType, ErrorResponseType, DataType>) {
  const initialState = {
    fetching: false,
    error: {},
    data: [],
    count: 0,
    params: {
      ...props.initialParameters,
    },
  };

  function reducer(
    state: StateTypes<DataType, ParametersType>,
    action: UseReducerActionType<UseTableActionKind>,
  ): StateTypes<DataType, ParametersType> {
    switch (action.type) {
