export function downloadfromCSVContent(csvContent: any, fileName: string | number) {
  csvContent = '\uFEFF' + csvContent;
  const csvData = new Blob([csvContent], {
    type: 'text/csv;charset=utf-8',
  });
  const csvUrl = URL.createObjectURL(csvData);
  const a = document.createElement('a');
  a.href = csvUrl;
  a.setAttribute('download', fileName + '.csv');
  document.body.appendChild(a);
  a.click();
  a.remove();
}

export function downloadAsCSV(rows: any[], fileName?: string | number, separator = ',') {
  const formattedFileName = fileName ? fileName : Date.now();
  const csvContent = rows.reduce(
    (content, row) => content + `${row.join(separator)}\n`,
    '',
  );
  downloadfromCSVContent(csvContent, formattedFileName);
}

