export function checkMandatoryFieldsInXLS(
  file: any,
  mandatoryColumns: string[],
  callback: (isValidXls: boolean) => void,
) {
  const reader = new FileReader();

  reader.onload = (e) => {
    const data = e?.target?.result;
    const workbook = XLSX.read(data, { type: 'binary' });
    const firstSheetName = workbook.SheetNames[0];
    const worksheet = workbook.Sheets[firstSheetName];
    const jsonData = XLSX.utils.sheet_to_json(worksheet) as Record<string, any>[];

    if (!jsonData || jsonData.length === 0) {
      callback(false);
      return;
    }

    const missingColumns = mandatoryColumns.filter(
      (col: string) => !Object.keys(jsonData[0]).includes(col),
    );

    const isValidXLS = missingColumns.length === 0;
    callback(isValidXLS);
  };

  reader.onerror = (e) => {
    callback(false);
  };

  reader.readAsBinaryString(file);
}

export function readXLSXFile(file: any, callback: (data: any) => void) {
  const reader = new FileReader();
  reader.onload = (evt) => {
    const data = evt.target?.result;
    const workbook = XLSX.read(data, { type: 'binary' });
    const wsname = workbook.SheetNames[0];
    const ws = workbook.Sheets[wsname];
    const returnData = XLSX.utils.sheet_to_json(ws, { header: 1 });
    if (returnData.length === 0) {
      callback([]);
    } else {
      const validData = returnData.filter((row: any, index: number) => (index > 0 && row.length > 0));
      callback(validData.flat());
    }
  };
  reader.readAsBinaryString(file);
}

