export function getDateRangeFilterParams({
  filterParams,
  dateRangeFilterKey = 'dateRange',
  fromDateKey = 'since',
  toDateKey = 'until',
  format,
}: {
  filterParams: Record<string, unknown>;
  dateRangeFilterKey?: string;
  fromDateKey?: string;
  toDateKey?: string;
  format?: string;
}) {
  const dateRangeArray = filterParams[dateRangeFilterKey];
  if (!Array.isArray(dateRangeArray) || !dateRangeArray.length) return {};
  const [startDate, endDate] = dateRangeArray as [Moment, Moment];
  const fromDate = moment.isMoment(startDate)
    ? moment(startDate).startOf('day').utc().format(format)
    : startDate;
  const toDate = moment.isMoment(endDate)
    ? moment(endDate).endOf('day').utc().format(format)
    : endDate;
  return {
    [fromDateKey]: fromDate,
    [toDateKey]: toDate,
  };
}

