export function reset<T extends object>({
  setValue,
  values,
  newValues,
}: ResetFormStateParamsType<T>): void {
  Object.keys(values).forEach((key) => {
    const typedKey = key as keyof T;
    if (newValues && key in newValues) {
      setValue(typedKey, newValues[typedKey]);
    } else {
      setValue(typedKey, undefined);
    }
  });
}

