  if (!value) {
    return Promise.reject(new Error('Required'));
  }

  if (value.trim().length === 0) {
    return Promise.reject(new Error('Not a valid name'));
  }

  return Promise.resolve();
}

export function nameAddressValidator(rule, value) {
  if (!value) {
    if (rule.field === 'address')
      return Promise.reject(
        new Error('Please fill up Bill & Collection POC Address to proceed'),
      );
    if (rule.field === 'name')
      return Promise.reject(
        new Error('Please fill up Bill & Collection POC Name to proceed'),
      );
  }

  if (value.length !== 0 && value.trim().length === 0) {
    return Promise.reject(new Error('Invalid Format'));
  }
  if (value.length !== 0 && rule.field === 'name') {
    if (
      rule.pattern &&
      (new RegExp(rule.pattern[0]).test(value[0]) ||
        new RegExp(rule.pattern[1]).test(value))
    ) {
      return Promise.reject(new Error('Enter valid Name'));
    }
  }
  if (value.length !== 0 && rule.field === 'address') {
    if (
      rule.pattern &&
      (new RegExp(rule.pattern[0]).test(value[0]) ||
        new RegExp(rule.pattern[1]).test(value))
    ) {
      return Promise.reject(new Error('Enter valid Address'));
    }
  }

  return Promise.resolve();
}

