}

export function wholeNumberValidator(rule, value) {
  if (rule.required && !value) {
    return Promise.reject('Required');
  }

  // min should be string
  // cause if min is number and value is 0
  // this condition will always fail
  // e.g. rules={[{ required: true, min: '0', validator: decimalNumberValidator }]}
  if (value) {
    if (rule.min && parseInt(value, 10) < parseInt(rule.min, 10)) {
      return Promise.reject(`Minimum value must be ${rule.min}`);
    }

    if (value && value % 1 != 0) {
      return Promise.reject('Only integer number is allowed');
    }
  }
  return Promise.resolve();
}

export function decimalNumberValidator(rule, value) {
  if (rule.required && !value) {
    return Promise.reject('Required');
  }

  if (rule.required === false && !value) {
    return Promise.resolve();
  }

  // min should be string
  // cause if min is number and value is 0
  // this condition will always fail
  // e.g. rules={[{ required: true, min: '0', validator: decimalNumberValidator }]}

  if (rule.min && parseInt(value, 10) < parseInt(rule.min, 10)) {
    return Promise.reject(`Minimum value must be ${rule.min}`);
  }

  if (isNumeric(value) === false) {
    console.log('isNumeric(value)', isNumeric(value), value);
    return Promise.reject('Only number is allowed');
  }

  return Promise.resolve();
}

export function stringLengthValidator(rule, value) {
  if (!rule.required && !value) {
    return Promise.resolve();
  }
  if (!value) {
    return Promise.reject(new Error('Required'));
  }
  if (value.trim().length > rule.max || value.trim().length < rule.min) {
    return Promise.reject(
      new Error(`Input length should be between ${rule.min} and ${rule.max}`),
    );
  }
  return Promise.resolve();
}

export function validName(rule, value) {
  if (!rule.required && !value) {
    return Promise.resolve();
  }
  if (!value || !value.trim()) {
    return Promise.reject(new Error('Required'));
  }
  if (new RegExp(/^[a-zA-Z\d .\-&/]+$/).test(value) === false) {
    return Promise.reject(new Error(`Invalid Name`));
  }
  return Promise.resolve();
}

export function bankAccountNumber(rule, value) {
  const maxLen = 60;
  if (!rule.required && !value) {
    return Promise.resolve();
  }
  if (!value) {
    return Promise.reject(new Error('Required'));
  }
  if (new RegExp(/^[\d.-]+$/).test(value) === false) {
    return Promise.reject(new Error(`Invalid Account Number`));
  }
  if (value.trim().length > maxLen) {
    return Promise.reject(new Error(`Input length should less than ${maxLen}`));
  }
  return Promise.resolve();
}

export function decimalNumberValidatorWithPrecision(precision) {
  return function (rule, value) {
    const parsedValues = value.toString().split('.');
    if (parsedValues.length > 2) {
      return Promise.reject(rule.message);
    }
    if (value.toString().length && parsedValues.at(0).length < 1) {
      return Promise.reject(rule.message);
    }
    if (parsedValues.length === 2 && parsedValues.at(-1).length > precision) {
      return Promise.reject(rule.message);
    }
    return decimalNumberValidator(rule, value);
  };
}

