  return items?.map((item) => {
    const value = notConvertIdToString ? item[idIdx] : item[idIdx]?.toString();
    const label = item[titleIdx];
    const extraData = extraIdx !== undefined ? { [extraIdx]: item[extraIdx] } : {};
    return {
      label,
      value,
      ...extraData,
    };
  });
};

