  if (!slaFilterRole) {
    return undefined;
  }
  const slaKeys = _.uniq(
    slaEscalationLevelDefs.reduce(
      (arr, levelDef) => [
        ...arr,
        ...levelDef.escalationLevelsWithSlaBreachKeys.reduce(
          (arrInner, slaBreachKeys) => [...arrInner, ...slaBreachKeys.slaBreachKeys],
          [],
        ),
      ],
      [],
    ),
  );

  return slaKeys;
}

export function convertUnsafeContent(unsafeText: any) {
  return unsafeText.replace(/[<>&'"]/g, function (c: any) {
    switch (c) {
      case '<':
        return '&lt;';
      case '>':
        return '&gt;';
      case '&':
        return '&amp;';
      case "'":
        return '&#x27;';
      case '"':
        return '&quot;';
      case '/':
        return '&#x2F;';
    }
  });
}

