export const resolveSalaryMonthYear = ({ month, year }) => {
  const zeroIndexedMonth = month - 1;
  let since;
  const until = moment()
    .tz('Asia/Dhaka')
    .set({ year, month: zeroIndexedMonth, date: 26 })
    .endOf('day')
    .set({ second: 0 }); //moment month is 0 indexed.
  if (month === 1) {
    since = moment()
      .tz('Asia/Dhaka')
      .set({ year: year - 1, month: 11, date: 27 })
      .startOf('day'); // 11 is december
  } else {
    since = moment()
      .tz('Asia/Dhaka')
      .set({ year, month: zeroIndexedMonth - 1, date: 27 })
      .startOf('day');
  }
  return { since, until };
};

export function normalizeUnderScoreString(str) {
  const strWithoutUnderscore = str.replace(/_/gi, ' ');

  return strWithoutUnderscore
    .toLowerCase()
    .split(' ')
    .map((word) => {
      return word.replace(word[0], word[0].toUpperCase());
    })
    .join(' ');
}

export const normalizePhoneNumber = (phone) => {
  if (!phone) {
    return null;
  }

  let phoneStr = phone.toString();

  if (!regexValidator.validPhone.test(phone.toString())) {
    return null;
  }

  if (!phoneStr.startsWith('880') && phoneStr.charAt(0) !== '0') {
    phoneStr = `0${phoneStr}`;
  }

  return `880${phoneStr.slice(-10)}`;
};

export function renderValueWithSpan({
  value,
  valueMatcher,
  index,
  paginationInfo,
  dataSource,
  key,
}) {
  const trueIndex = index + paginationInfo.pageSize * (paginationInfo.current - 1);

  const obj = {
    children: value,
    props: {},
  };

  const matchWith = valueMatcher || value;

  if (index >= 1 && matchWith === dataSource[trueIndex - 1][key]) {
    obj.props.rowSpan = 0;
  } else {
    for (
      let i = 0;
      trueIndex + i !== dataSource.length && matchWith === dataSource[trueIndex + i][key];
      i += 1
    ) {
      obj.props.rowSpan = i + 1;
    }
  }
  return obj;
}

export const getTimeInMinsAndSecs = (time: number) => {
  const hours = Math.floor(time / (60 * 60));
  const minutes = Math.floor(time / 60) - hours * 60;
  // const seconds = time % 60;

  return `${formatTime(hours)}h: ${formatTime(minutes)}m`;
};

