const useInterval = (callback, delay) => {
  const savedCallback = useRef();
  useEffect(() => {
    savedCallback.current = callback;
  }, [callback]);
  useEffect(() => {
    const handler = (...args) => savedCallback.current(...args);

    if (delay !== null) {
      const id = setInterval(handler, delay);
      return () => clearInterval(id);
    }
  }, [delay]);
};

export function filterUndefined(obj) {
  return Object.entries(obj).reduce(
    (acc, [k, v]) => (v === undefined ? acc : { ...acc, [k]: v }),
    {},
  );
}

export const required = (_, value) => {
  if (!value && value !== 0) return Promise.reject('Required');
  return Promise.resolve();
};

export const dynamicRequired = (enabled = true) => (_, value) => {
  if (!enabled) return Promise.resolve();
  if (!value && value !== 0) return Promise.reject('Required');
  return Promise.resolve();
};

export const isNumber = (_, value) => {
  if (isNaN(value)) return Promise.reject('Must be number');
  return Promise.resolve();
};

export const isNaturalNumber = (_, value) => {
  if (!value) return Promise.resolve();
  value = value.toString();
  var n1 = Math.abs(value),
    n2 = parseInt(value, 10);
  if (!isNaN(n1) && n2 === n1 && n1.toString() === value) return Promise.resolve();
  return Promise.reject('Must be a positive number');
};

export const isValidPercentage = (_, value) => {
  if (!value) return Promise.resolve();
  value = value.toString();
  let number = parseInt(value);
  if (!isNaN(number) && number >= 0 && number <= 100) return Promise.resolve();
  return Promise.reject('Invalid percentage');
};

export const isValidNumericalPercentage = (_, value) => {
  if (!value) return Promise.resolve();
  value = value.toString();
  let number = parseInt(value);
  var n1 = Math.abs(value);
  if (
    !isNaN(number) &&
    number === n1 &&
    number >= 0 &&
    number <= 100 &&
    n1.toString() === value
  )
    return Promise.resolve();
  return Promise.reject('Invalid percentage');
};

export const isPositiveNumber = (_, value) => {
  if (!value) return Promise.resolve();
  value = value.toString();
  var n1 = Math.abs(value),
    n2 = parseInt(value, 10);
  if (!isNaN(n1) && n2 === n1 && n1.toString() === value && n1 > 0)
    return Promise.resolve();
  return Promise.reject('Must be a positive number');
};

export const isValidPhoneNumber = (value) => {
  value = value.toString();
  if (regexValidator.validPhone.test(value)) return true;
  return false;
};

export const isValidTime = (_, value) => {
  value = value.toString();
  if (/^(?:2[0-3]|[01][0-9]):[0-5][0-9]:[0-5][0-9]$/.test(value))
    return Promise.resolve();
  else return Promise.reject('Invalid Time');
};

export const isOnlyNaturalNumber = (_, value) => {
  value = value.toString();
  var n1 = Math.abs(value),
    n2 = parseInt(value, 10);
  if (!isNaN(n1) && n2 === n1 && n1.toString() === value) return Promise.resolve();
  return Promise.reject('Must be a number');
};

export const isValidValue = (value, allowEmptyArray = true) => {
  const isEmpty = value !== undefined && value !== null && value !== '';
  if (allowEmptyArray) return isEmpty;
  return isEmpty && value !== [];
};

export const isValidBillingCycle = (_, value) => {
  if (!value) return Promise.resolve();

  const num = Number(value);
  if (Number.isInteger(num) && num >= 1 && num <= 30) {
    return Promise.resolve();
  }

  return Promise.reject('Billing cycle must be between 1 and 30');
};

export default (App) => {
  return class AppWithRedux extends React.Component {
    static async getInitialProps(appContext) {
      // Get or Create the store with `undefined` as initialState
      // This allows you to set a custom default initialState
      const reduxStore = getOrCreateStore();

      // Provide the store to getInitialProps of pages
      appContext.ctx.reduxStore = reduxStore;

      let appProps = {};
      if (typeof App.getInitialProps === 'function') {
        appProps = await App.getInitialProps(appContext);
      }

      return {
        ...appProps,
        initialReduxState: reduxStore.getState(),
      };
    }

    constructor(props) {
      super(props);
      this.reduxStore = getOrCreateStore(props.initialReduxState);
    }

    render() {
      return <App {...this.props} reduxStore={this.reduxStore} />;
    }
  };
};

