const ReceiveCutoff = () => {
  const router = useRouter();
  const [selectedExtraTime, setSelectedExtraTime] = useState();
  const dispatch = useDispatch();
  const [form] = Form.useForm();
  const { rehydrated } = useSelector((state) => state._persist);
  const { cutOffSettings, getTimingsReq, editTimingsReq } = useSelector(
    (state) => state.cutoff,
  );
  const [editKey, setEditKey] = useState(undefined);
  const isEditing = !isNaN(editKey);

  useEffect(() => {
    if (!rehydrated) return;
    dispatch(getTimingsAsync(router.query.type));
    return setEditKey;
  }, [router.query.type]);

  const setEditMode = (key, row) => {
    setEditKey(key);
    if (!row) return;
    form.setFieldsValue({
      CUTOFF_CONFIGURATION_ID: row.CUTOFF_CONFIGURATION_ID,
      START_TIME: moment(row.START_TIME, TIME_FORMAT),
      END_TIME: moment(row.END_TIME, TIME_FORMAT),
      TYPE: row.TYPE,
    });
  };

  const onSubmit = useCallback(() => {
    const edit = async () => {
      const fields = form.getFieldsValue(true);

      let payload = [];
      const makePayload = (cutoff) => ({
        CUTOFF_CONFIGURATION_ID: cutoff.CUTOFF_CONFIGURATION_ID,
        START_TIME: fields.START_TIME.format('HH:mm:SS'),
        END_TIME: fields.END_TIME.format('HH:mm:SS'),
      });

      if (fields.TYPE === 'receive') {
        payload = cutOffSettings.map(makePayload);
      } else if (fields.TYPE === 'dispatch') {
        payload.push(makePayload(fields));
      } else {
        return message.error(
          `Error, field type was neither receive/dispatch. It was: ${fields.TYPE}.`,
        );
      }

      message.loading({ content: 'Saving...', key: 'edit' });
      const { error } = await dispatch(editTimingsAsync(payload));

      if (!error) {
        message.success({ content: 'Saved!', key: 'edit' }, 0);
        setEditMode(); // hides form
        dispatch(getTimingsAsync(router.query.type));
      } else {
        message.error({ content: 'Failed to save', key: 'edit' });
        console.log('> Edit Error', error);
      }
    };
    edit();
  }, [cutOffSettings, router.query.type]);

  let pageTitle = 'Receive Cutoff';
  if (router.query.type === 'dispatch') {
    pageTitle = 'Dispatch Cutoff';
  }

  const showExtraTimeModal = (row) => {
    // Allow to extend time only AFTER passing the end time
    if (moment().diff(moment(row.END_TIME, 'HH:mm:SS')) < 0) {
      return message.error(
        `Error: Not allowed to extend before ${moment(row.END_TIME, 'HH:mm:SS').format(
          'h:mm a',
        )}`,
      );
    }
    setSelectedExtraTime(row.CUTOFF_CONFIGURATION_ID);

