const getScannedItemIdsAsync = createAsyncThunk(
  'scanner/getScannedItemIdsAsync',
  async (params: GetItemFromRedisType) => {
    let localScannedIds: { [key: string]: number } = {};
    let redisScannedIds: { [key: string]: number } = {};

    // Get from localStorage
    if (params.key) {
      const localData = localStorage.getItem(params.key);
      if (localData) {
        try {
          localScannedIds = JSON.parse(localData);
        } catch (e) {
          console.error('Error parsing localStorage data:', e);
        }
      }
    }

    // Get from Redis
    try {
      const response = await redisAPI.getItemFromRedis(params);
      redisScannedIds = response?.data || {};
    } catch (e) {
      console.error('Error fetching from Redis:', e);
    }

    // Merge both sources, taking the maximum count for each item
    const mergedScannedIds: { [key: string]: number } = { ...redisScannedIds };
    Object.keys(localScannedIds).forEach((itemId) => {
      if (mergedScannedIds[itemId]) {
        // If item exists in both, take the maximum count
        mergedScannedIds[itemId] = Math.max(
          mergedScannedIds[itemId],
          localScannedIds[itemId],
        );
      } else {
        // If item only exists in localStorage, add it
        mergedScannedIds[itemId] = localScannedIds[itemId];
      }
    });

    return mergedScannedIds;
  },
);

const scanItemIdAsync = createAsyncThunk(
  'scanner/scanItemIdAsync',
  async (
    body: {
      scannedItemId: string;
      sourcePanel?: string;
      hubId?: number;
      isBulk?: boolean;
    },
    thunkAPI: ThunkApiType,
  ) => {
    const state = thunkAPI.getState();
    const scannedIds = { ...state.scannedIds };
    const formattedListOfItems = getListFromLoadedOrSavedItems(
      state.items,
      state.savedItems,
    );
    const formattedScannedItem = formattedListOfItems.find((item: LogisticsParcelsType) =>
      checkValidScannedItem(body.scannedItemId, item, state.scanAdditionalProperty),
    );
    const scannedMainId = formattedScannedItem
      ? formattedScannedItem?.id
      : body.scannedItemId;
    const found = formattedListOfItems.find(
      (item: LogisticsParcelsType) =>
        item[state.scanMatchProperty] === body.scannedItemId ||
        checkValidScannedItem(body.scannedItemId, item, state.scanAdditionalProperty),
    );
    if (!found) {
      sounds.play('error');
      alert('Invalid scan');
      if (body.sourcePanel && body.hubId && !body.isBulk) {
        scanParcelLogs({
          parcelId: body.scannedItemId,
          sourcePanel: body.sourcePanel,
          hubId: body.hubId,
        });
      }

      if (body.isBulk) {
        scanParcelBulkLogs({
          bulkIds: [body.scannedItemId],
          sourcePanel: body.sourcePanel,
          hubId: body.hubId,
        });
      }
      return scannedIds;
    }
    if (!state.acceptDuplicateItems && scannedIds[scannedMainId] === 1) {
      sounds.play('error');
      message.warn(`Already scanned. ${body.scannedItemId}`);
      return scannedIds;
    } else {
      if (state.scanAdditionalProperty.length > 0 && scannedMainId) {
        scannedIds[scannedMainId] = scannedIds[scannedMainId] || 0;
        scannedIds[scannedMainId]++;
      } else {
        scannedIds[body.scannedItemId] = scannedIds[body.scannedItemId] || 0;
        scannedIds[body.scannedItemId]++;
      }
    }

    // Store in localStorage instead of calling Redis API
    localStorage.setItem(state.redisKey, JSON.stringify(scannedIds));
    return scannedIds;
  },
);

const bulkScanItemIdAsync = createAsyncThunk(
  'scanner/bulkScanItemIdAsync',
  async (scannedItemIds: string[], thunkAPI: ThunkApiType) => {
    const state = thunkAPI.getState();
    const scannedIds = { ...state.scannedIds };
    const formattedListOfItems = getListFromLoadedOrSavedItems(
      state.items,
      state.savedItems,
    );
    let hasAnyValidScan = false;
    for (const scannedItemId of scannedItemIds) {
      const formattedScannedItem = formattedListOfItems.find(
        (item: LogisticsParcelsType) =>
          checkValidScannedItem(scannedItemId, item, state.scanAdditionalProperty),
      );
      const scannedMainId = formattedScannedItem
        ? formattedScannedItem.id
        : scannedItemId;
      const found = formattedListOfItems.find(
        (item: LogisticsParcelsType) =>
          item[state.scanMatchProperty] === scannedItemId ||
          checkValidScannedItem(scannedItemId, item, state.scanAdditionalProperty),
      );
      if (!found) {
        sounds.play('error');
        alert(`Invalid scan: ${scannedItemId}`);
        continue;
      }
      if (!state.acceptDuplicateItems && scannedIds[scannedMainId] === 1) {
        sounds.play('error');
        message.warn(`Already scanned: ${scannedItemId}`);
        continue;
      }
      hasAnyValidScan = true;
      if (state.scanAdditionalProperty.length > 0 && scannedMainId) {
        scannedIds[scannedMainId] = scannedIds[scannedMainId] || 0;
        scannedIds[scannedMainId]++;
      } else {
        scannedIds[scannedItemId] = scannedIds[scannedItemId] || 0;
        scannedIds[scannedItemId]++;
      }
    }
    if (!hasAnyValidScan) {
      return scannedIds; // Nothing valid, no need to persist
    }
    // Store in localStorage instead of calling Redis API
    localStorage.setItem(state.redisKey, JSON.stringify(scannedIds));
    return scannedIds;
  },
);

const removeScanItemIdAsync = createAsyncThunk(
  'scanner/removeScanItemIdAsync',
  async (
    payload: string | { removedItemId: string; enableRedisSync?: boolean },
    thunkAPI: ThunkApiType,
  ) => {
    // Support both old (string) and new (object) signature
    const removedItemId = typeof payload === 'string' ? payload : payload.removedItemId;
    const enableRedisSync =
      typeof payload === 'string' ? true : payload.enableRedisSync ?? true;

    const state = thunkAPI.getState();
    const scannedIds = { ...state.scannedIds };
    scannedIds[removedItemId]--;
    if (scannedIds[removedItemId] === 0) {
      delete scannedIds[removedItemId];
    }
    // Store in localStorage or remove if empty
    if (Object.keys(scannedIds).length === 0) {
      localStorage.removeItem(state.redisKey);
    } else {
      localStorage.setItem(state.redisKey, JSON.stringify(scannedIds));
    }

    // Sync to Redis if enabled
    if (enableRedisSync) {
      try {
        await redisAPI.setItemInRedis({
          isGlobal: state.isGlobal,
          key: state.redisKey,
          value: JSON.stringify(scannedIds),
        } as any);
      } catch (error) {
        console.error('Error syncing to Redis:', error);
      }
    }

    return scannedIds;
  },
);

