const removeItemsAsync = createAsyncThunk(
  'scanner/removeItemsAsync',
  async function (
    payload: string[] | { removedIds: string[]; enableRedisSync?: boolean },
    thunkAPI: ThunkApiType,
  ) {
    // Support both old (array) and new (object) signature
    const removedIds = Array.isArray(payload) ? payload : payload.removedIds;
    const enableRedisSync = Array.isArray(payload)
      ? true
      : payload.enableRedisSync ?? true;

    const state = thunkAPI.getState();
    const allScannedIds = { ...state.scannedIds };
    const removedIdsMap = arrayToCountMap(removedIds);
    const uniqueRemovedIds = [...(new Set(removedIds) as any)];
    const restOfScannedIds = uniqueRemovedIds?.reduce((obj, itemId) => {
      allScannedIds[itemId] -= removedIdsMap[itemId];
      if (allScannedIds[itemId] === 0) {
        delete allScannedIds[itemId];
      }
      return obj;
    }, allScannedIds);

    // Store in localStorage or remove if empty
    if (Object.keys(restOfScannedIds).length === 0) {
      localStorage.removeItem(state.redisKey);
    } else {
      localStorage.setItem(state.redisKey, JSON.stringify(restOfScannedIds));
    }

    // Sync to Redis if enabled
    if (enableRedisSync) {
      try {
        await redisAPI.setItemInRedis({
          isGlobal: state.isGlobal,
          key: state.redisKey,
          value: JSON.stringify(restOfScannedIds),
        } as any);
      } catch (error) {
        console.error('Error syncing to Redis:', error);
      }
    }

    return { removedIdsMap, restOfScannedIds };
  },
);

