const syncLocalStorageToRedisAsync = createAsyncThunk(
  'scanner/syncLocalStorageToRedisAsync',
  async (_, thunkAPI: ThunkApiType) => {
    const state = thunkAPI.getState();

    // Get scanned IDs from localStorage
    const localData = localStorage.getItem(state.redisKey);
    if (!localData) {
      message.info('No local data to sync');
      return state.scannedIds;
    }

    let scannedIds: { [key: string]: number };
    try {
      scannedIds = JSON.parse(localData);
    } catch (e) {
      console.error('Error parsing localStorage data:', e);
      message.error('Failed to parse local storage data');
      return state.scannedIds;
    }

    // Convert scanned IDs object to array of IDs (expanding duplicates)
    const scannedItemIds: string[] = [];
    Object.keys(scannedIds).forEach((itemId) => {
      const count = scannedIds[itemId];
      for (let i = 0; i < count; i++) {
        scannedItemIds.push(itemId);
      }
    });

    if (scannedItemIds.length === 0) {
      message.info('No items to sync');
      return state.scannedIds;
    }

    // Sync to Redis using bulk scan
    try {
      const result = await redisAPI.setItemInRedis({
        isGlobal: state.isGlobal,
        key: state.redisKey,
        value: JSON.stringify(scannedIds),
      } as any);
      return result;
    } catch (error) {
      console.error('Error syncing to Redis:', error);
      message.error('Failed to sync data to server');
      throw error;
    }
  },
);

