const useScanner = ({ initialState, selectors, logger, loadScanned }: UseScannerType) => {
  if (!initialState.redisKey) {
    throw Error(`initialState for useScanner() must provide a 'redisKey' property.`);
  }
  const persist = useSelector((state: PersistReducerstateType) => state._persist);
  sounds = useSounds();

  /* useReducer */
  const [reducedState, dispatch] = useReducer<any>(ScannerSlice.reducer, {
    ...INITIAL_STATE,
    ...initialState,
  });
  const state = reducedState as ScannerSliceInitialStateType;

  let betterDispatch = supportThunk(dispatch, () => state);
  betterDispatch = supportLogging(betterDispatch, logger);

  const selections = Object.keys(selectors)?.reduce(
    (obj: Partial<LogisticsParcelsType>, key) => {
      obj[key] = selectors[key](state);
      return obj;
    },
    {},
  );

  useEffect(() => {
    if (!state.loadInitialItems) return;
    if (!persist.rehydrated) return;
    if (initialState.isScannedItemGrouped) {
      betterDispatch(
        getScannedItemsWithGroupAsync({
          isGlobal: initialState.isGlobal,
          key: initialState.redisKey,
        }),
      );
      return;
    }
    betterDispatch(
      getScannedItemIdsAsync({
        isGlobal: initialState.isGlobal,
        key: initialState.redisKey,
      }),
    );
  }, []);

  useEffect(() => {
    if (state.itemsTimeout && typeof loadScanned === 'function' && state.initialLoad) {
      loadScanned(state.scannedIds ? Object.keys(state.scannedIds) : []);
    }
  }, [state.scannedIds, state.itemsTimeout]);

  return { selections, dispatch: betterDispatch };
};

