const bulkScanItemIdAsync = createAsyncThunk(
  'scanner/bulkScanItemIdAsync',
  async (scannedItemIds: string[], thunkAPI: ThunkApiType) => {
    const state = thunkAPI.getState();
    const scannedIds = { ...state.scannedIds };
    const formattedListOfItems = getListFromLoadedOrSavedItems(
      state.items,
      state.savedItems,
    );
    let hasAnyValidScan = false;
    for (const scannedItemId of scannedItemIds) {
      const formattedScannedItem = formattedListOfItems.find(
        (item: LogisticsParcelsType) =>
          checkValidScannedItem(scannedItemId, item, state.scanAdditionalProperty),
      );
      const scannedMainId = formattedScannedItem
        ? formattedScannedItem.id
        : scannedItemId;
      const found = formattedListOfItems.find(
        (item: LogisticsParcelsType) =>
          item[state.scanMatchProperty] === scannedItemId ||
          checkValidScannedItem(scannedItemId, item, state.scanAdditionalProperty),
      );
      if (!found) {
        sounds.play('error');
        alert(`Invalid scan: ${scannedItemId}`);
        continue;
      }
      if (!state.acceptDuplicateItems && scannedIds[scannedMainId] === 1) {
        sounds.play('error');
        message.warn(`Already scanned: ${scannedItemId}`);
        continue;
      }
      hasAnyValidScan = true;
      if (state.scanAdditionalProperty.length > 0 && scannedMainId) {
        scannedIds[scannedMainId] = scannedIds[scannedMainId] || 0;
        scannedIds[scannedMainId]++;
      } else {
        scannedIds[scannedItemId] = scannedIds[scannedItemId] || 0;
        scannedIds[scannedItemId]++;
      }
    }
    if (!hasAnyValidScan) {
      return scannedIds; // Nothing valid, no need to persist
    }
    return await redisAPI.setItemInRedis({
      isGlobal: state.isGlobal,
      key: state.redisKey,
      value: scannedIds,
    });
  },
);

