  'scanner/fetchScannedItemIdsAsync',
  async (_, thunkAPI: ThunkApiType) => {
    const state = thunkAPI.getState();
    return await redisAPI.getItemFromRedis({
      isGlobal: state.isGlobal,
      key: state.redisKey,
    });
  },
);

const removeItemsAsync = createAsyncThunk(
  'scanner/removeItemsAsync',
  async function (removedIds: string[], thunkAPI: ThunkApiType) {
    const state = thunkAPI.getState();
    const allScannedIds = { ...state.scannedIds };
    const removedIdsMap = arrayToCountMap(removedIds);
    const uniqueRemovedIds = [...(new Set(removedIds) as any)];
    const restOfScannedIds = uniqueRemovedIds?.reduce((obj, itemId) => {
      allScannedIds[itemId] -= removedIdsMap[itemId];
      if (allScannedIds[itemId] === 0) {
        delete allScannedIds[itemId];
      }
      return obj;
    }, allScannedIds);

    await redisAPI.setItemInRedis({
      isGlobal: state.isGlobal,
      key: state.redisKey,
      value: restOfScannedIds,
    });
    return { removedIdsMap, restOfScannedIds };
  },
);

const scanItemIdsAsync = createAsyncThunk(
  'scanner/scanItemIdsAsync',
  async (
    body: {
      scannedItemIds: string[];
      sourcePanel?: string;
      hubId?: number;
      bulkTransferIds?: string[];
    },
    thunkAPI: ThunkApiType,
  ) => {
    const state = thunkAPI.getState();
    const scannedIds = { ...state.scannedIds };
    body.scannedItemIds?.forEach((scannedItemId) => {
      scannedIds[scannedItemId] = scannedIds[scannedItemId] || 0;
      scannedIds[scannedItemId]++;
    });
    if (body.sourcePanel && body.hubId) {
      scanParcelBulkLogs({
        bulkIds: body.bulkTransferIds,
        sourcePanel: body.sourcePanel,
        hubId: body.hubId,
      });
    }

    return redisAPI.setItemInRedis({
      isGlobal: state.isGlobal,
      key: state.redisKey,
      value: scannedIds,
    });
  },
);

const loadScannedItemAsync = createAsyncThunk(
  'scanner/loadScannedItemAsync',
  async ({ scannedItemId }: LoadScannedItemPayloadType, thunkAPI: ThunkApiType) => {
    const state = thunkAPI.getState();
    const scannedIds = { ...state.scannedIds };

    const sounds = {
      // success: new Audio('/audio/success-tmqpx6.mp3'),
      error: new Audio('/audio/error-p8nnll.mp3'),
    };

    if (!state.acceptDuplicateItems && scannedIds[scannedItemId] === 1) {
      message.warn(`Already scanned. ${scannedItemId}`);
      sounds.error.play();
      return scannedIds;
    } else {
      scannedIds[scannedItemId] = scannedIds[scannedItemId] || 0;
      scannedIds[scannedItemId]++;
    }

    return redisAPI.setItemInRedis({
      isGlobal: state.isGlobal,
      key: state.redisKey,
      value: scannedIds,
    });
  },
);

const loadBulkScannedItemAsync = createAsyncThunk(
  'scanner/loadBulkScannedItemAsync',
  async ({ scannedItemIds }: LoadBulkScannedItemPayloadType, thunkAPI: ThunkApiType) => {
    const state = thunkAPI.getState();
    const scannedIds = { ...state.scannedIds };

    const sounds = {
      // success: new Audio('/audio/success-tmqpx6.mp3'),
      error: new Audio('/audio/error-p8nnll.mp3'),
    };

    for (const id of scannedItemIds) {
      if (!state.acceptDuplicateItems && scannedIds[id] === 1) {
        message.warn(`Already scanned. ${id}`);
        sounds.error.play();
        continue;
      }

      scannedIds[id] = scannedIds[id] || 0;
      scannedIds[id]++;
    }

    return redisAPI.setItemInRedis({
      isGlobal: state.isGlobal,
      key: state.redisKey,
      value: scannedIds,
    });
  },
);

