async function getTimings(TYPE) {
  let times = await ax.get(`/v1/logistics/cut-off/configurations`, {
    params: { TYPE },
  });

  if ('cutOffSettings' in times) {
    times = times.cutOffSettings.map((tm) => ({
      ...tm,
      START_TIME: setTimezoneToLocal(tm.START_TIME),
      END_TIME: setTimezoneToLocal(tm.END_TIME),
      UPDATED_END_TIME: tm.UPDATED_END_TIME
        ? setTimezoneToLocal(tm.UPDATED_END_TIME)
        : tm.UPDATED_END_TIME,
    }));
  }
  return times;
}

function editTimings(payload) {
  const cutoffs = payload.map((tm) => ({
    ...tm,
    START_TIME: tm.START_TIME ? setTimezoneToUtc(tm.START_TIME) : undefined,
    END_TIME: tm.END_TIME ? setTimezoneToUtc(tm.END_TIME) : undefined,
  }));

  console.log('> edit payload', cutoffs);
  return ax.put(`/v1/logistics/cut-off/configurations`, cutoffs);
}

