function Counter({ from = 0, to }) {
  const nodeRef = useRef();

  useEffect(() => {
    const node = nodeRef.current;

    const controls = animate(from, to, {
      duration: 1,
      onUpdate(value) {
        node.textContent = new Intl.NumberFormat('en-BD').format(value.toFixed(2));
      },
    });

    return () => controls.stop();
  }, [from, to]);

  return <p ref={nodeRef} style={{ margin: 0 }} />;
}

function AreaApprovalActions({ id, trackingId }) {
  const [confirmLoading, setConfirmLoading] = useState(false);
  const remarksRef = useRef(undefined);

  const dispatch = useDispatch();
  const handleConfirm = useCallback(
    async (status, remarks) => {
      try {
        setConfirmLoading(true);
        await approvalRequestApi.updateApprovalRequest({ id, status, comments: remarks });
        message.success(`Successfully marked as ${status}`);
        dispatch(approvalRequestRemove({ id }));
      } catch (error) {
        setConfirmLoading(false);
        message.error(error?.message || 'Something went wrong.');
      }
    },
    [id, dispatch],
  );

  return (
    <Space>
      <Button
        data-testid="disapprove-button-area-approval"
        size="small"
        onClick={() => {
          Modal.confirm({
            title: 'Are you sure',
            content: (
              <div>
                <p>Are you sure you want to disapprove the area for {trackingId}?</p>
                <p style={{ marginTop: 10, marginBottom: 0 }}>Set Remarks</p>
                <TextArea
                  data-testid="disapprove-reason-area-approval"
                  autoSize={{ minRows: 4, maxRows: 5 }}
                  placeholder="Enter your remarks here"
                  value={remarksRef.current}
                  onChange={(e) => {
                    remarksRef.current = e.target.value;
                  }}
                />
              </div>
            ),
            okText: 'Confirm',
            cancelText: 'Cancel',
            onCancel: () => {
              remarksRef.current = undefined;
            },
            confirmLoading,
            onOk: () => {
              if (!remarksRef.current || !remarksRef.current.trim()) {
                message.error('Remarks are required before confirming!');
                return Promise.reject();
              }
              handleConfirm(approvalRequestStatus.disapproved, remarksRef.current);
              remarksRef.current = undefined;
              return Promise.resolve();
            },
          });
        }}>
        Disapprove
      </Button>
      <Button
        data-testid="approve-button-area-approval"
        size="small"
        type="primary"
        onClick={() => {
          Modal.confirm({
            title: 'Are you sure',
            content: `Are you sure you want to approve the area for ${trackingId}?`,
            okText: 'Confirm',
            cancelText: 'Cancel',
            confirmLoading,
            onOk: () => handleConfirm(approvalRequestStatus.approved),
          });
        }}>
        Approve
      </Button>
    </Space>
  );

