function AreaRevertAction({ id, trackingId, fetchData }) {
  const [confirmLoading, setConfirmLoading] = useState(false);

  const handleConfirm = useCallback(async () => {
    try {
      setConfirmLoading(true);
      await approvalRequestApi.revertApprovalRequest(id);
      message.success(`Successfully reverted.`);
      fetchData();
    } catch (error) {
      setConfirmLoading(false);
      message.error(error?.message || 'Something went wrong.');
    }
  }, [id, fetchData]);

  return (
    <Space>
      <Button
        onClick={() => {
          Modal.confirm({
            title: 'Are you sure',
            content: `Are you sure you want to revert the area request for ${trackingId}?`,
            okText: 'Confirm',
            cancelText: 'Cancel',
            confirmLoading,
            onOk: () => handleConfirm(),
          });
        }}>
        Revert
      </Button>
    </Space>
  );
}

function AreaChangeModal({
  isModalOpen,
  onModalClose,
  defaultValue,
  onSubmit,
  isSubmitting,
  title = 'Change area',
  placeholder = 'Enter area name',
  loadAreas = defaultLoadAreas,
}) {
  const defaultValues = useMemo(() => {
    return {
      area: defaultValue
        ? { value: defaultValue.areaId, label: defaultValue.area }
        : null,
    };
  }, [defaultValue]);

  const form = useForm({
    defaultValues,
    resolver: yupResolver(validationSchema),
  });

  const { errors, handleSubmit, control, reset } = form;

  useEffect(() => {
    reset({ ...defaultValues });
  }, [reset, isModalOpen, defaultValues]);

  const onSubmitForm = (values) => {
    onSubmit(values, { reset, errors });
  };

  return (
    <Modal
      title={title}
      maskClosable={false}
      visible={isModalOpen}
      onCancel={onModalClose}
      footer={null}>
      <form onSubmit={handleSubmit(onSubmitForm)}>
        <FormLabel label="New area" />
        <Controller
          name="area"
          control={control}
          placeholder={placeholder}
          as={<ReactSelect isAsync />}
          errors={errors}
          cacheOptions
          defaultOptions
          loadOptions={loadAreas}
        />
        <Space size={12} style={{ marginTop: 20 }}>
          <Button disabled={isSubmitting} onClick={onModalClose}>
            Cancel
          </Button>
          <Button
            type="primary"
            htmlType="submit"
            loading={isSubmitting}
            disabled={isSubmitting}>
            Confirm
          </Button>
        </Space>
      </form>
    </Modal>
  );
}

const AddHubConfigModal: React.FC<AreaProps> = ({ area, visible, onOk, onCancel }) => {
  const hubResolutionConfigTypes = useGetHubResolutionConfigs();

  const renderResolutionHubConfigTypes = hubResolutionConfigTypes.map(
    (hub: HubResolutionConfigInterface) => {
      return hub.CONFIG_CODE;
    },
  );

  const [form] = Form.useForm();

  const hubNames = area
    ? Array.from(
        new Set(
          area.HUBS.map((hub) => {
            return hub.HUB_NAME;
          }),
        ),
      )
    : [];

  const findHubConfigId = (hubType: string) => {
    const hub = hubResolutionConfigTypes.filter((hub: HubResolutionConfigInterface) => {
      if (hub.CONFIG_CODE === hubType) return hub;
    });
    return hub[0].ID;
  };

  const findHubId = (hubType: string) => {
    const hub = area.HUBS.filter((hub) => {
      if (hub.HUB_NAME == hubType) return hub;
    });
    return hub[0].HUB_ID;
  };

  const handleConfirm = () => {
    form.submit();
  };

  const handleSubmit = async () => {
    onOk();
    const hub = form.getFieldsValue();
    const hubId = findHubId(hub.hubName);
    const hubConfigId = findHubConfigId(hub.hubConfigType);
    const res: AxiosResponse<SuccessResponseType> = await hubAPI.addHubConfig(
      area.ID,
      hubId,
      hubConfigId,
    );

    if (!res.data.isError) {
      notification.success({
        message: 'Hub Config added successfully!',
        placement: 'bottomRight',
      });
    }
    if (res.data.isError) {
