const HubStatusToggle = ({ areaId, hub, onChange }: StatusToggleType) => {
  const [status, setStatus] = useState(hub.STATUS);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    setStatus(hub.STATUS);
  }, [hub]);

  const handleClick = async () => {
    setIsLoading(true);
    try {
      await hubAPI.setHubStatus(hub, areaId, status);
      setStatus(status === 'active' ? 'inactive' : 'active');
      onChange();
    } catch (error: any) {
      message.error(error.message || 'Something went wrong on the server');
    }
    setIsLoading(false);
  };

  return (
    <Switch loading={isLoading} onClick={handleClick} checked={status === 'active'} />
  );
};

