        return (
          <Option key={key} value={key}>
            {item['hubName']}
          </Option>
        );
      }),
    [hubs],
  );

  let timeout;
  let currentValue;

  const fetch = (value, callback) => {
    if (timeout) {
      clearTimeout(timeout);
      timeout = null;
    }
    currentValue = value;

    const searchAreas = async () => {
      if (value.length <= 2) return;

      let matchedAreas = await areaAPI.searchAreasV2(value, { isOperational: 1 });

      matchedAreas = matchedAreas.map((ar) => {
        return {
          groupLabel: ar.DISTRICT_NAME,
          groupId: ar.DISTRICT_ID,
