export function Authorize({ roles = [], permissionIds = [], children }) {
  const isAuthorized = useAuthorization({ roles, permissionIds });

  return isAuthorized ? children : null;
}

export function PermissonBasedAuthorize({ permissionId, children }) {
  const { userPermissions } = useSelector((state) => state.auth);

  const isAuthorized = useMemo(() => {
    if (!permissionId) return true;

    return (userPermissions || []).find(
      (userPermission) => userPermission.id === permissionId,
    );
  }, [userPermissions, permissionId]);

  return isAuthorized ? children : null;
}

