const C2CDelete = ({
  action,
  consignment,
  handlePostDeleteActions,
  panel,
}: {
  action?: string;
  consignment?: Record<string, any>;
  handlePostDeleteActions?: () => void;
  panel: string;
}) => {
  const [isLoading, setLoading] = useState(false);
  const dispatch = useDispatch<DispatchType>();
  const handleDeleteConsignment = async () => {
    if (consignment) {
      setLoading(true);
      const deleteResponse = await dispatch(
        deleteConsignmentAsync({
          consignmentId: consignment.id,
          source:
            panel === PAGE_TYPE.consignmentPanel ? PAGE_TYPE.consignmentPanel : undefined,
        }),
      );
      if (!('error' in deleteResponse) && handlePostDeleteActions) {
        handlePostDeleteActions();
      }
    }
    setLoading(false);
  };

  const [isBulkDeleteModalOpen, setBulkDeleteModalOpen] = useState(false);
  const rolesToBeAuthorized =
    panel === PAGE_TYPE.consignmentPanel
      ? ['Redx C2C Sales Agent']
      : ['Offline C2C Delete Admin'];

  return (
    <Authorize roles={rolesToBeAuthorized}>
      {action === 'bulk' && (
        <>
          <Button key={0} type="primary" onClick={() => setBulkDeleteModalOpen(true)}>
            Bulk Delete
          </Button>
          <FileUploadModal
            sourceOfUpload="c2c-bulk-delete"
            visible={isBulkDeleteModalOpen}
            handleModalClose={() => setBulkDeleteModalOpen(false)}
          />
        </>
      )}
      {consignment && !consignment.dispatchedOnDate && (
        <Popconfirm
          title={`Are you sure you want to delete Consignment C/N ${consignment.invoiceNumberUi}?`}
          onConfirm={handleDeleteConsignment}
          okText="Yes"
          cancelText="No">
          <Button loading={isLoading}>
            <DeleteFilled />
          </Button>
        </Popconfirm>
      )}
    </Authorize>
  );
};

