export const ChargeReducer = (state, action) => {
  switch (action.type) {
    case 'MINIMUM_CHARGE':
      return { ...state, minimumCharge: action.payload.charge };
    case 'EDIT_CHARGE':
      return {
        ...state,
        conditionCharge: action.payload.condition,
        totalCharge: action.payload.total,
      };
    case 'ONCHANGE_CONDITION_CHARGE':
      return {
        ...state,
        conditionCharge: action.payload.condition,
        totalCharge: action.payload.total,
      };
    case 'TOTAL_CHARGE':
      return { ...state, totalCharge: action.payload.total };
    default:
      return state;
  }
};

