const CashReconAgentWiseModal = ({ isModalOpen, onModalClose, hubId, from, to }) => {
  const [reconData, setRecondata] = useState([]);
  const [isFetching, setIsFetching] = useState(true);
  const columns = [
    {
      title: 'Delivery Agent Name',
      dataIndex: 'NAME',
    },
    {
      title: 'Total COD Shipment Assigned (TK)',
      dataIndex: 'TOTAL_COD_SHIPMENTS_ASSIGNED',
    },
    {
      title: 'Total COD Shipment Delivered (TK)',
      dataIndex: 'TOTAL_COD_SHIPMENTS_DELIVERED',
    },
    {
      title: 'COD Amount to be Collected (TK)',
      dataIndex: 'TOTAL_COD_SHIPMENTS_DELIVERED',
    },
    {
      title: 'Actual Amount Collected (TK)',
      dataIndex: 'AMOUNT_COLLECTED',
    },
    {
      title: 'Pending Amount (TK)',
      dataIndex: 'DIFFERENCE_AMOUNT',
    },
  ];
  const getAgentWiseSummary = async (hubId, from, to) => {
    if (moment.isMoment(from)) from = from.format('x');
    if (moment.isMoment(to)) to = to.format('x');
    setIsFetching(true);
    const res = await cashReconAPI.getAgentWiseSummary({ hubId, from, to });
    if (res.isError === true) {
      setIsFetching(false);
      message.error('Something went wrong !');
      onModalClose();
      return;
    }
    const agentSummary = res.agentSummaryHubWise.map((summary) => {
      summary.DIFFERENCE_AMOUNT =
        summary.TOTAL_COD_SHIPMENTS_DELIVERED - summary.AMOUNT_COLLECTED;
      return summary;
    });
    setRecondata(agentSummary);
    setIsFetching(false);
  };
  useEffect(() => {
    if (isModalOpen === true) getAgentWiseSummary(hubId, from, to);
  }, [isModalOpen]);
  return (
    <Modal
      title="Agent Wise Cash Reconciliation data for hub"
      visible={isModalOpen}
      destroyOnClose={true}
      onCancel={() => {
        setRecondata([]);
        onModalClose();
      }}
      width={1000}
      footer={null}>
      <Table
        rowKey={(row) => row.NAME}
        dataSource={reconData}
        columns={columns}
        loading={isFetching}
        pagination={{
          hideOnSinglePage: true,
        }}
        scroll={{ y: 500 }}
      />
    </Modal>
  );
};

