const CombinedInvoicesCsvExportFilters = ({
  csvFiltersForm,
  handleCsvFiltersFormSubmit,
}: {
  csvFiltersForm: FormInstance;
  handleCsvFiltersFormSubmit: (values: combinedInvoicesCsvFiltersType) => void;
}) => {
  const [disableMethodOptions, setDisableMethodOptions] = useState(false);

  const handleValuesChange = (changedValues: any) => {
    if (changedValues.method) {
      if (changedValues.method === 'City Bank') {
        csvFiltersForm.setFieldsValue({ methodOptions: 'FT' });
        setDisableMethodOptions(false);
        return;
      }

      if (changedValues.method === 'Dhaka Bank') {
        csvFiltersForm.setFieldsValue({ methodOptions: 'BFTN' });
        setDisableMethodOptions(false);
        return;
      }

      csvFiltersForm.setFieldsValue({ methodOptions: undefined });
      setDisableMethodOptions(true);
    }
  };

  return (
    <Form
      form={csvFiltersForm}
      css={combined_invoices_export_csv_filters_css}
      onValuesChange={handleValuesChange}
      onFinish={handleCsvFiltersFormSubmit}>
      <Form.Item className="form-item" name="banner">
        <Select
          options={businessTypeOptions}
          allowClear
          placeholder="Select type"
          data-testid="CombinedInvoicesCsvExportFilters_BusinessType"
