      </Form.Item>

      <Form.Item className="form-item" name="methodOptions">
        <Select
          options={fundTransferOptions}
          allowClear
          disabled={disableMethodOptions}
          placeholder="Select Method Option"
          data-testid="CombinedInvoicesCsvExportFilters_FundTransferMethod"
