const InvoiceShopSelectionModal = ({
  modalState,
  onClose,
  exportInvoice,
}: {
  modalState: any;
  onClose: () => void;
  exportInvoice: (shopIds: number[], filterParams: any) => void;
}) => {
  const [selectedShopIds, setSelectedShopIds] = useState<number[]>([]);

  if (!modalState.isOpen) {
    return null;
  }

  const handleChange = (checkedValues: any[]) => {
    setSelectedShopIds(checkedValues);
  };

  const handleClose = () => {
    setSelectedShopIds([]);
    onClose();
  };

  const handleOk = () => {
    console.log('Selected shopIds: ', selectedShopIds);
    exportInvoice(selectedShopIds, modalState.data.filterParams);
    handleClose();
  };

  return (
    <Modal
      visible={modalState.isOpen}
      title="Select Combined Invoice Shops"
      onCancel={handleClose}
      okButtonProps={{ disabled: modalState.data.shops.length === 0 }}
      onOk={handleOk}
      centered
