const AddExtraTimeModal = (props) => {
  const router = useRouter();
  const dispatch = useDispatch();
  const { visible, hide, cutoffId } = props;
  const [form] = Form.useForm();
  const [isBusy, setBusy] = useState(false);
  const { cutOffSettings } = useSelector((state) => state.cutoff);
  const cutoff = useMemo(() => {
    return cutOffSettings.find((ct) => ct.CUTOFF_CONFIGURATION_ID === cutoffId);
  }, [cutoffId, cutOffSettings]);

  console.log('> cutoff', cutoff, cutoffId);
  useEffect(() => {
    if (!cutoff) return;
    form.setFieldsValue({
      CUTOFF_CONFIGURATION_ID: cutoff.CUTOFF_CONFIGURATION_ID,
      EXTRA_TIME: cutoff.EXTRA_TIME,
    });
  }, [cutoff, cutoffId, form]);

  const editExtraTime = useCallback(() => {
    const edit = async () => {
      const fields = form.getFieldsValue(true);

      let payload = [];
      const makePayload = (ct) => ({
        CUTOFF_CONFIGURATION_ID: ct.CUTOFF_CONFIGURATION_ID,
        EXTRA_TIME: fields.EXTRA_TIME ? fields.EXTRA_TIME : undefined,
      });

      if (cutoff.TYPE === 'receive') {
        payload = cutOffSettings.map(makePayload);
      } else if (cutoff.TYPE === 'dispatch') {
        payload.push(makePayload(fields));
      } else {
        return message.error(
          `Error, field type was neither receive/dispatch. It was: ${cutoff.TYPE}.`,
        );
      }

      message.loading({ content: 'Saving...', key: 'edit' });
      setBusy(true);
      const { error } = await dispatch(editTimingsAsync(payload));
      if (!error) {
        message.success({ content: 'Saved!', key: 'edit' }, 0);
        dispatch(getTimingsAsync(router.query.type));
        hide(); // hides modal
      } else {
        message.error({ content: 'Failed to save', key: 'edit' });
        console.log('> Edit Error', error);
      }
      setBusy(false);
    };
    edit();
  }, [cutOffSettings, cutoff, dispatch, form, hide, router.query.type]);

  const resetModal = useCallback(() => {
    setBusy(false);
  }, []);

  return (
    <Modal
      title="Set extra time"
      visible={visible}
      onOk={editExtraTime}
      onCancel={hide}
      okText="Save"
      confirmLoading={isBusy}
      destroyOnClose={true} /* Only for children */
      afterClose={resetModal} /* For the modal itself */
      width={280}>
      <Form layout="vertical" form={form} onFinish={editExtraTime}>
        <Form.Item name="EXTRA_TIME" className="no-margin">
          <InputNumber
            size="large"
            placeholder="Extra time (in minutes)"
            min={1}
            style={{ width: '100%' }}
          />
        </Form.Item>

