  setExcelFile,
  pdfFile,
  excelFile,
  pdfLabel,
  excelLabel,
}: UploadReferenceTemplatePropType) => {
  const [pdfLoading, setPdfLoading] = useState(false);
  const [excelLoading, setExcelLoading] = useState(false);
  const router = useRouter();

  const handlePaymentRefPdfUpload = async (pdfInfo: UploadChangeParam<UploadFile>) => {
    const { file } = pdfInfo;
    if (file.status === 'uploading') {
      setPdfLoading(true);
      return;
    }
    if (file.status === 'done') {
      setPdfFile(file);
      setPdfLoading(false);
      return;
    }
    if (file.status === 'error') {
      message.error(`File upload failed`);
      setPdfLoading(false);
    }
  };

  const handleMarkAsPaidUpload = (excelInfo: UploadChangeParam<UploadFile>) => {
    const { file } = excelInfo;
    if (file.status === 'uploading') {
      setExcelLoading(true);
      return;
    }
    if (file.status === 'done') {
      setExcelFile(file);
      setExcelLoading(false);
      return;
    }
    if (file.status === 'error') {
      message.error(`File upload failed`);
      setExcelLoading(false);
    }
  };
  return (
    <>
      <PageHeader
        className="pageHeader"
        title={title}
        data-testid="upload-reference-title"
      />
      <section className="mainContent" data-testid="pdf-upload-section">
        <FileUpload
          multiple={false}
          maxCount={1}
          showUploadList={false}
          accept=".pdf"
          onChange={handlePaymentRefPdfUpload}
          label={pdfLabel}
          placeholder="Upload file"
          disabled={pdfLoading}
          withCredentials
        />
        <p css={supportedText}>{`Supported format (.pdf)`}</p>
        {pdfFile && (
          <FilePreview
            fileName={pdfFile.name}
            removeUploadedFile={() => setPdfFile(undefined)}
          />
        )}
      </section>
      <section className="mainContent" data-testid="sample-download-combined-invoices">
        <p css={download_css}>Download the sample file</p>
        <span style={{ marginRight: 10 }}>Click on the following link</span>

