function FinanceReportFilter({ onSubmit, isFetching }) {
  const merchantTypeOptions = useMemo(() => getSelectOptions(merchantTypes), []);
  const reportTypeOptions = useMemo(() => getSelectOptions(reportTypes), []);

  const handleSubmit = ({ merchantType, reportType, dateRange }) => {
    const [from, to] = dateRange;
    onSubmit({
      merchantType,
      reportType,
      from,
      since: to,
    });
  };

  return (
    <div css={filters_css}>
      <Form layout="inline" onFinish={handleSubmit}>
        <Form.Item
          name="merchantType"
          rules={[
            {
              required: true,
              message: 'Required.',
            },
          ]}>
          <Select style={{ width: 200 }} placeholder="Merchant Type">
            {merchantTypeOptions}
          </Select>
        </Form.Item>
        <Form.Item
          name="reportType"
          rules={[
            {
              required: true,
              message: 'Required.',
            },
          ]}>
          <Select style={{ width: 150 }} placeholder="Report Type">
            {reportTypeOptions}
          </Select>
        </Form.Item>
        <Form.Item
          name="dateRange"
          rules={[
            () => ({
              validator(_, value) {
                const [from, to] = value || [];

                if (!value) {
                  return Promise.reject('Required.');
                }

                if (to.diff(from, 'months') < 1) {
                  return Promise.resolve();
                }

                return Promise.reject('Range should be within 1 month.');
              },
            }),
          ]}>
          <DateRangePicker />
        </Form.Item>
        <Form.Item>
          <Button disabled={isFetching} type="primary" htmlType="submit">
            Filter
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
}

