export function ReturnConfirmModal({
  modalTitle,
  isOpen,
  closeModal,
  onReturnConfirm,
  ReturnConfirmForm,
  submittingOtp,
  trackingId,
  reasonId,
}: {
  modalTitle: string;
  isOpen: boolean;
  closeModal: () => void;
  onReturnConfirm: (values: any) => void;
  ReturnConfirmForm: FormInstance<any>;
  submittingOtp: boolean;
  trackingId: string;
  reasonId: number;
}) {
  const [remainingTime, setRemainingTime] = useState(60 * 10);
  const [apiCalling, setApiCalling] = useState(false);

  useTimeInterval(() => {
    setRemainingTime(remainingTime - 60);
  }, 1000 * 60);

  const requestOtp = useCallback(async () => {
    const response = ((await ParcelService.parcelReturnOtpRequest(
      trackingId,
      reasonId,
      'ADMIN_PANEL',
    )) as unknown) as { isError: boolean; ttl: number };

    const diff = moment().diff(response.ttl, 'seconds');
    setRemainingTime(Math.abs(diff));
  }, [reasonId, trackingId]);

  useEffect(() => {
    (async () => {
      setApiCalling(true);

      const existingReturnOtpTtlResponse = (await ((await ParcelService.getReturnOtpTTL(
        trackingId,
        'merchant',
      )) as unknown)) as { isError: boolean; ttl: number; message?: string };

      if (existingReturnOtpTtlResponse.isError) {
        message.error(
          existingReturnOtpTtlResponse.message || 'Something went wrong at the server',
        );

        setApiCalling(false);
        return;
      }

      if (existingReturnOtpTtlResponse.ttl <= 0) {
        await requestOtp();
      } else {
        const diff = moment().diff(existingReturnOtpTtlResponse.ttl, 'seconds');
        setRemainingTime(Math.abs(diff));
      }

      setApiCalling(false);
    })();
  }, [trackingId, reasonId, requestOtp]);

  return (
    <Modal
      destroyOnClose
      data-testid="HubApproverParcels_ReturnConfirmModal_2"
      visible={isOpen}
      title={modalTitle}
      onCancel={closeModal}
      footer={null}
      bodyStyle={{ paddingBottom: 16 }}>
      <Space direction="vertical" size={16}>
        <div>
          <Typography.Title
            data-testid="HubApproverParcels_ReturnConfirmModal_Body_Title"
            level={5}

