const HubFilterForm = ({
  setFinalHubs,
  hubs,
}: {
  setFinalHubs: React.Dispatch<StartHubType[]>;
  hubs: StartHubType[];
}) => {
  const [hubSearchForm] = Form.useForm();

  const filterByHubName = (hubs: StartHubType[], hubName: string) => {
    return hubs.filter((hub) =>
      hub.hubName.toLowerCase().includes(hubName.toLowerCase()),
    );
  };

  const filterByHubType = (hubs: StartHubType[], hubType: string) => {
    return hubs.filter((hub) => hub.hubType.includes(hubType));
  };

  const searchHubs = (values: { hubName: string; hubType: string }) => {
    let filteredHubs = hubs;

    if (values.hubName) {
      filteredHubs = filterByHubName(filteredHubs, values.hubName);
    }

    if (values.hubType) {
      filteredHubs = filterByHubType(filteredHubs, values.hubType);
    }

    setFinalHubs(filteredHubs);
  };

  const handleHubFilterClear = () => {
    hubSearchForm.resetFields();
    setFinalHubs(hubs);
  };

  return (
    <Form
      form={hubSearchForm}
      onValuesChange={(_, allValues) => searchHubs(allValues)}
      name="searchHub"
      size="large">
      <Row gutter={[12, 12]}>
        <Col span={4}>
          <HubNameFormItem />
        </Col>

        <Col span={5}>
          <HubTypeFormItem hubTypeList={hubTypes} />
        </Col>

        <Col span={2}>
          <Button
            type="primary"
            data-testid="hubFilterClear"
            onClick={() => handleHubFilterClear()}>
            Clear
          </Button>
        </Col>
      </Row>
    </Form>
  );
};

export const returnLabelString = (label: string) => {
  const labelType =
    label === 'POD'
      ? 'IS_POD_UPLOADED'
      : label === 'Trip'
      ? 'IS_VEHICLE_INFORMATION_UPDATED'
      : 'IS_CHALAN_UPLOADED';
  return labelType;
};

export const checkInvalid = (problemList: ReconcileDataType[], label: string) => {
  const labelType = returnLabelString(label);
  const invalid = problemList?.filter((item) => item[labelType] === false);
  return invalid;
};

