export const checkValidTrip = (problemList: ReconcileDataType[]) => {
  const noVehicleOrders = problemList?.filter(
    (item) => item.IS_VEHICLE_REQUIRED === false,
  );
  const emptyTripsForNoVehicle = noVehicleOrders?.filter(
    (item) => item.TRIP_IDS.length > 0,
  );
  if (
    problemList?.length === noVehicleOrders?.length &&
    emptyTripsForNoVehicle?.length === 0
  ) {
    return true;
  }
};

export const checkValidTripStatus = (
  problemList: ReconcileDataType[],
  validationType: string,
) => {
  if (checkInvalid(problemList, validationType)?.length === 0) return true;
  else {
    const noVehicleOrders = problemList?.filter(
      (item) => item.IS_VEHICLE_REQUIRED === false,
    );
    const emptyTripsForNoVehicle = noVehicleOrders?.filter(
      (item) => item.TRIP_IDS.length > 0,
    );
    if (
      problemList?.length === noVehicleOrders?.length &&
      emptyTripsForNoVehicle?.length === 0
    ) {
      return true;
    }
  }
  return false;
};

const CashReceiveConfirmationModal = ({
  open,
  closeReceiveConfirmModal,
  nonReconcileData,
}: CashReceiveValidateProps) => {
  const challanInvalid = checkInvalid(nonReconcileData, 'Challan') || [];
  const podInvalid = checkInvalid(nonReconcileData, 'POD') || [];
  const tripValid = checkValidTripStatus(nonReconcileData, 'Trip');

  return (
    <Modal
      title={<b>Cash cannot be confirmed</b>}
      visible={open}
      onCancel={closeReceiveConfirmModal}
      maskClosable={true}
      footer={null}
      width={550}>
      <SubReceiveModalContentForTrip
        tripText="Trip"
        list={nonReconcileData}
        valid={tripValid}
      />
      <SubReceiveModalContentForTrip
        tripText="Challan"
        extraText="Trip"
        valid={challanInvalid?.length <= 0 || checkValidTrip(nonReconcileData)}
        list={nonReconcileData}
      />
      <SubReceiveModalContent
        titleText="POD"
        validInvalid={podInvalid?.length <= 0}
        list={nonReconcileData}
      />
    </Modal>
  );
};

