export const useTimeInterval = (callback, delay) => {
  const otpTimerSavedCallback = useRef();

  useEffect(() => {
    otpTimerSavedCallback.current = callback;
  }, [callback]);

  useEffect(() => {
    function setupInterval() {
      otpTimerSavedCallback.current();
    }
    if (delay !== null) {
      let timer = setInterval(setupInterval, delay);
      return () => clearInterval(timer);
    }
  }, [delay]);
};

