  };

  const getTimeInMinsAndSecs = (time) => {
    let minutes = Math.floor(time / 60);
    let seconds = time % 60;

    return `${formatTime(minutes)}: ${formatTime(seconds)}`;
  };

  const handleChange = (element, index, keyCode) => {
    if (isNaN(element.value)) return false;
    setOtp([
      ...otp.map((d, idx) => {
