    if (element.nextSibling && element.value !== '' && keyCode !== 8) {
      element.nextSibling.focus();
    }
    if (element.previousSibling && element.value === '') {
      element.previousSibling.focus();
    }
  };

  const otpResetArray = resetOtpInitialArray(otpLength);
